import { useContext, useState } from 'react'
import CartContext from '../Store/Cart-Context'

import classes from './Cart.module.css'
import mealItems from './AvailableMeals.module.css'
import Modal from '../UI/Modal'
import Card from '../UI/Card'

let Cart = (props)=>{

let cartCtx = useContext(CartContext)

let itemsDisplay = cartCtx.items.map((data)=>{
  console.log(data,'cart dta')
  return(<>{data.name} {data.amount} {data.price}</>)
})

let cartTotal = cartCtx.totalAmount.toFixed(2)

return(
    <>
    <Modal close={props.close}>
  <div className={classes['cart-items']}>
    <ul>
      {itemsDisplay}
    </ul>
</div>
    <div className={classes.total}>
       Total Amount: {cartTotal}
    </div>
    <div className={classes.actions}>
    <button className={classes['button--alt']}> Close </button>
    {cartTotal >= 10 && <button className={classes.button}> Order </button>}
    </div>
    </Modal>
    </>
)
}

export default Cart
